integer             ia, ib, ix
real                ra, rb, rx
doubleprecision     da, db, dx
complex             ma, mb, mx
doublecomplex       na, nb, nx

ix = ia + ib
ix = ia - ib
ix = ia * ib
ix = ia / ib
ix = ia ** ib
ix = -ia

rx = ra + rb
rx = ra - rb
rx = ra * rb
rx = ra / rb
rx = ra ** rb
rx = -ra

dx = da + db
dx = da - db
dx = da * db
dx = da / db
dx = da ** db
dx = -da

mx = ma + mb
mx = ma - mb
mx = ma * mb
mx = ma / mb
mx = ma ** mb
