      SUBROUTINE AAAA
      implicit none


      INTEGER n1, i
      INTEGER i3, i2, i1
      INTEGER a1b, a2b, a3b
      INTEGER b1b, b2b, b3b





      i=n1-1
      do i3=a3b,b3b
      do i2=a2b,b2b
      do i1=a1b,b1b
         i=i+1
      end do
      end do
      end do

      return
      END SUBROUTINE AAAA
